
public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	// Week 2 Assignment for Promineo Tech Coding Boot Camp by Joe
	
	// Step 5 Part A
		int U = 0;
		
			while (U <= 100) {
		
				System.out.println(U);
			
					U = U + 2;
		}
	// Step 5 Part B
	
		int i = 100;
		
			while (i >= 0) {
		
				System.out.println(i);
		
					i = i - 3; 
		}
		
	// Step 5 Part C
				
		for (int a = 1; a <= 100; a++) {
		if (a % 2 == 1) {
		 
			System.out.println(a);
		
		}
		}
		
	// Step 5 Part D
		
	for (int t = 0; t <= 100; t++) {
	
		if (t % 15 == 0) {
			
			System.out.println("Hello World"); }
			
			else if (t % 3 == 0) {
				
				System.out.println("Hello");}
			
			else if (t % 5 == 0) {
				
				System.out.println("World"); }
			
			else {
					System.out.println(t);
				}
			
		}
			
	}

	}
	


